mapper.select:
SELECT ANN_NUM, ANN_TITLE, ANN_CONTENT, ANN_FILE, ANN_DATE
  FROM BOARD_ANNOUNCE
 WHERE ANN_NUM = #{annNum}

mapper.insert:


  SELECT BOARD_ANNOUNCE_SEQ.NEXTVAL
    FROM DUAL

INSERT INTO BOARD_ANNOUNCE (
    ANN_NUM
  , ANN_TITLE
  , ANN_CONTENT
  , ANN_FILE
  , ANN_DATE
) VALUES (
    #{annNum}
  , #{annTitle}
  , #{annContent}
  , #{annFile}
  , SYSDATE
)
